pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title CitizenIdentityRegistry
 * @dev Manages verified citizen identities and their verification levels
 * @author CitiProof Team
 */
contract CitizenIdentityRegistry is Ownable, ReentrancyGuard {
    // Counter for citizen IDs (using simple uint256 instead of Counters)
    uint256 private _citizenIdCounter;

    // Verification levels
    enum VerificationLevel {
        UNVERIFIED,    // Default state
        BASIC,         // Basic wallet connection + EFP
        VERIFIED,      // Enhanced verification (50+ EFP followers)
        PREMIUM        // Government-verified or high reputation
    }

    // Citizen profile structure
    struct CitizenProfile {
        uint256 citizenId;
        address walletAddress;
        string ensName;              // ENS name if available
        string efpTokenId;           // EFP list token ID
        uint256 efpFollowers;        // EFP follower count
        uint256 efpFollowing;        // EFP following count
        VerificationLevel verificationLevel;
        uint256 registrationTimestamp;
        uint256 lastUpdateTimestamp;
        bool isActive;
        uint256 reputationScore;     // Link to reputation contract
        string ipfsMetadata;         // Additional profile data on IPFS
    }

    // Storage mappings
    mapping(address => uint256) public walletToCitizenId;
    mapping(uint256 => CitizenProfile) public citizens;
    mapping(string => uint256) public ensNameToCitizenId;
    mapping(string => bool) public registeredEnsNames;

    // Events
    event CitizenRegistered(
        uint256 indexed citizenId,
        address indexed walletAddress,
        string ensName,
        VerificationLevel verificationLevel
    );

    event CitizenVerificationUpdated(
        uint256 indexed citizenId,
        address indexed walletAddress,
        VerificationLevel oldLevel,
        VerificationLevel newLevel
    );

    event CitizenProfileUpdated(
        uint256 indexed citizenId,
        address indexed walletAddress,
        string ensName,
        string efpTokenId
    );

    event CitizenDeactivated(
        uint256 indexed citizenId,
        address indexed walletAddress,
        address indexed deactivatedBy
    );

    // Modifiers
    modifier onlyRegisteredCitizen() {
        require(walletToCitizenId[msg.sender] != 0, "CitizenRegistry: Caller is not a registered citizen");
        require(citizens[walletToCitizenId[msg.sender]].isActive, "CitizenRegistry: Citizen account is not active");
        _;
    }

    modifier validCitizenId(uint256 _citizenId) {
        require(_citizenId > 0 && _citizenId <= _citizenIdCounter, "CitizenRegistry: Invalid citizen ID");
        _;
    }

    modifier onlyActiveCitizen(uint256 _citizenId) {
        require(citizens[_citizenId].isActive, "CitizenRegistry: Citizen is not active");
        _;
    }

    constructor(address initialOwner) Ownable(initialOwner) {}

    /**
     * @dev Register a new citizen with flexible requirements
     * @param _ensName ENS name (can be empty string for wallet-only registration)
     * @param _efpTokenId EFP list token ID (can be empty string)
     * @param _efpFollowers Number of EFP followers (0 allowed for new users)
     * @param _efpFollowing Number of EFP following (0 allowed for new users)
     * @param _ipfsMetadata IPFS hash for additional profile data
     */
    function registerCitizen(
        string memory _ensName,
        string memory _efpTokenId,
        uint256 _efpFollowers,
        uint256 _efpFollowing,
        string memory _ipfsMetadata
    ) external nonReentrant {
        require(walletToCitizenId[msg.sender] == 0, "CitizenRegistry: Wallet already registered");

        // Check if ENS name is already taken (if provided)
        if (bytes(_ensName).length > 0) {
            require(!registeredEnsNames[_ensName], "CitizenRegistry: ENS name already registered");
        }

        // Increment citizen ID counter
        _citizenIdCounter++;
        uint256 newCitizenId = _citizenIdCounter;

        // Determine verification level based on EFP data
        VerificationLevel verificationLevel = _determineVerificationLevel(_efpFollowers, _efpFollowing);

        // Create citizen profile
        citizens[newCitizenId] = CitizenProfile({
            citizenId: newCitizenId,
            walletAddress: msg.sender,
            ensName: _ensName,
            efpTokenId: _efpTokenId,
            efpFollowers: _efpFollowers,
            efpFollowing: _efpFollowing,
            verificationLevel: verificationLevel,
            registrationTimestamp: block.timestamp,
            lastUpdateTimestamp: block.timestamp,
            isActive: true,
            reputationScore: 0, // Will be updated by reputation contract
            ipfsMetadata: _ipfsMetadata
        });

        // Update mappings
        walletToCitizenId[msg.sender] = newCitizenId;
        if (bytes(_ensName).length > 0) {
            ensNameToCitizenId[_ensName] = newCitizenId;
            registeredEnsNames[_ensName] = true;
        }

        emit CitizenRegistered(newCitizenId, msg.sender, _ensName, verificationLevel);
    }

    /**
     * @dev Simplified registration for wallet-only users (hackathon-friendly)
     * Automatically generates ENS subdomain and placeholder EFP data
     */
    function registerCitizenSimple() external nonReentrant {
        require(walletToCitizenId[msg.sender] == 0, "CitizenRegistry: Wallet already registered");

        // Generate automatic ENS subdomain using last 6 chars of address
        string memory addressSuffix = Strings.toHexString(uint160(msg.sender) & 0xFFFFFF, 3);
        string memory autoEnsName = string(abi.encodePacked("citizen", addressSuffix, ".citiproof.eth"));

        // Ensure generated name is unique (highly unlikely to collide)
        require(!registeredEnsNames[autoEnsName], "CitizenRegistry: Generated ENS name collision");

        // Create with minimal verification data (can be updated later)
        _citizenIdCounter++;
        uint256 newCitizenId = _citizenIdCounter;

        citizens[newCitizenId] = CitizenProfile({
            citizenId: newCitizenId,
            walletAddress: msg.sender,
            ensName: autoEnsName,
            efpTokenId: "", // Can be added later
            efpFollowers: 0,
            efpFollowing: 0,
            verificationLevel: VerificationLevel.UNVERIFIED,
            registrationTimestamp: block.timestamp,
            lastUpdateTimestamp: block.timestamp,
            isActive: true,
            reputationScore: 0,
            ipfsMetadata: ""
        });

        // Update mappings
        walletToCitizenId[msg.sender] = newCitizenId;
        ensNameToCitizenId[autoEnsName] = newCitizenId;
        registeredEnsNames[autoEnsName] = true;

        emit CitizenRegistered(newCitizenId, msg.sender, autoEnsName, VerificationLevel.UNVERIFIED);
    }

    /**
     * @dev Update citizen's EFP verification data
     * @param _efpTokenId New EFP token ID
     * @param _efpFollowers Updated follower count
     * @param _efpFollowing Updated following count
     */
    function updateEFPVerification(
        string memory _efpTokenId,
        uint256 _efpFollowers,
        uint256 _efpFollowing
    ) external onlyRegisteredCitizen {
        uint256 citizenId = walletToCitizenId[msg.sender];
        CitizenProfile storage citizen = citizens[citizenId];

        VerificationLevel oldLevel = citizen.verificationLevel;
        VerificationLevel newLevel = _determineVerificationLevel(_efpFollowers, _efpFollowing);

        // Update EFP data
        citizen.efpTokenId = _efpTokenId;
        citizen.efpFollowers = _efpFollowers;
        citizen.efpFollowing = _efpFollowing;
        citizen.verificationLevel = newLevel;
        citizen.lastUpdateTimestamp = block.timestamp;

        if (oldLevel != newLevel) {
            emit CitizenVerificationUpdated(citizenId, msg.sender, oldLevel, newLevel);
        }

        emit CitizenProfileUpdated(citizenId, msg.sender, citizen.ensName, _efpTokenId);
    }

    /**
     * @dev Update citizen's ENS name
     * @param _ensName New ENS name
     */
    function updateENSName(string memory _ensName) external onlyRegisteredCitizen {
        require(bytes(_ensName).length > 0, "CitizenRegistry: ENS name cannot be empty");
        require(!registeredEnsNames[_ensName], "CitizenRegistry: ENS name already registered");

        uint256 citizenId = walletToCitizenId[msg.sender];
        CitizenProfile storage citizen = citizens[citizenId];

        // Remove old ENS name from registry
        if (bytes(citizen.ensName).length > 0) {
            registeredEnsNames[citizen.ensName] = false;
            delete ensNameToCitizenId[citizen.ensName];
        }

        // Set new ENS name
        citizen.ensName = _ensName;
        citizen.lastUpdateTimestamp = block.timestamp;
        ensNameToCitizenId[_ensName] = citizenId;
        registeredEnsNames[_ensName] = true;

        emit CitizenProfileUpdated(citizenId, msg.sender, _ensName, citizen.efpTokenId);
    }

    /**
     * @dev Update reputation score (only called by reputation contract)
     * @param _citizenId Citizen ID
     * @param _reputationScore New reputation score
     */
    function updateReputationScore(
        uint256 _citizenId,
        uint256 _reputationScore
    ) external validCitizenId(_citizenId) {
        // TODO: Add access control for reputation contract
        // require(msg.sender == reputationContract, "CitizenRegistry: Only reputation contract can update scores");

        citizens[_citizenId].reputationScore = _reputationScore;
        citizens[_citizenId].lastUpdateTimestamp = block.timestamp;

        // Check if reputation score qualifies for premium verification
        if (_reputationScore >= 1000 && citizens[_citizenId].verificationLevel != VerificationLevel.PREMIUM) {
            VerificationLevel oldLevel = citizens[_citizenId].verificationLevel;
            citizens[_citizenId].verificationLevel = VerificationLevel.PREMIUM;
            emit CitizenVerificationUpdated(
                _citizenId, 
                citizens[_citizenId].walletAddress, 
                oldLevel, 
                VerificationLevel.PREMIUM
            );
        }
    }

    /**
     * @dev Deactivate a citizen account (admin function)
     * @param _citizenId Citizen ID to deactivate
     */
    function deactivateCitizen(uint256 _citizenId) external onlyOwner validCitizenId(_citizenId) {
        citizens[_citizenId].isActive = false;
        citizens[_citizenId].lastUpdateTimestamp = block.timestamp;

        emit CitizenDeactivated(
            _citizenId,
            citizens[_citizenId].walletAddress,
            msg.sender
        );
    }

    /**
     * @dev Reactivate a citizen account (admin function)
     * @param _citizenId Citizen ID to reactivate
     */
    function reactivateCitizen(uint256 _citizenId) external onlyOwner validCitizenId(_citizenId) {
        citizens[_citizenId].isActive = true;
        citizens[_citizenId].lastUpdateTimestamp = block.timestamp;
    }

    // View functions
    function getCitizen(uint256 _citizenId) external view validCitizenId(_citizenId) returns (CitizenProfile memory) {
        return citizens[_citizenId];
    }

    function getCitizenByWallet(address _wallet) external view returns (CitizenProfile memory) {
        uint256 citizenId = walletToCitizenId[_wallet];
        require(citizenId != 0, "CitizenRegistry: Wallet not registered");
        return citizens[citizenId];
    }

    function getCitizenByENS(string memory _ensName) external view returns (CitizenProfile memory) {
        uint256 citizenId = ensNameToCitizenId[_ensName];
        require(citizenId != 0, "CitizenRegistry: ENS name not registered");
        return citizens[citizenId];
    }

    function isCitizenRegistered(address _wallet) external view returns (bool) {
        return walletToCitizenId[_wallet] != 0 && citizens[walletToCitizenId[_wallet]].isActive;
    }

    function getTotalCitizens() external view returns (uint256) {
        return _citizenIdCounter;
    }

    function getVerificationLevel(address _wallet) external view returns (VerificationLevel) {
        uint256 citizenId = walletToCitizenId[_wallet];
        require(citizenId != 0, "CitizenRegistry: Wallet not registered");
        return citizens[citizenId].verificationLevel;
    }

    function isENSNameAvailable(string memory _ensName) external view returns (bool) {
        return !registeredEnsNames[_ensName];
    }

    // Internal functions
    function _determineVerificationLevel(uint256 _followers, uint256 _following) internal pure returns (VerificationLevel) {
        if (_followers == 0 && _following == 0) {
            return VerificationLevel.UNVERIFIED;
        } else if (_followers < 50) {
            return VerificationLevel.BASIC;
        } else {
            return VerificationLevel.VERIFIED;
        }
    }

    // Batch operations for efficiency
    function getCitizensBatch(uint256[] memory _citizenIds) 
        external 
        view 
        returns (CitizenProfile[] memory) 
    {
        CitizenProfile[] memory profiles = new CitizenProfile[](_citizenIds.length);
        for (uint256 i = 0; i < _citizenIds.length; i++) {
            if (_citizenIds[i] > 0 && _citizenIds[i] <= _citizenIdCounter) {
                profiles[i] = citizens[_citizenIds[i]];
            }
        }
        return profiles;
    }

    function getActiveCitizensCount() external view returns (uint256) {
        uint256 activeCount = 0;
        for (uint256 i = 1; i <= _citizenIdCounter; i++) {
            if (citizens[i].isActive) {
                activeCount++;
            }
        }
        return activeCount;
    }
}","keccak256":"0x558f50048caa6b475ee31972fae16eee3985777f19dbd9882a6d86277223d533","license":"MIT"},"contracts/GovernmentProjectRegistry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./CitizenIdentityRegistry.sol";

/**
 * @title GovernmentProjectRegistry
 * @dev Manages all government projects and their lifecycle
 * @author CitiProof Team
 */
contract GovernmentProjectRegistry is Ownable, ReentrancyGuard {

    CitizenIdentityRegistry public immutable citizenRegistry;

    // Project status enumeration
    enum ProjectStatus {
        PROPOSED,      // Project proposed but not approved
        APPROVED,      // Project approved and funded
        IN_PROGRESS,   // Project execution started
        COMPLETED,     // Project completed successfully
        CANCELLED,     // Project cancelled
        UNDER_REVIEW   // Project under audit/review
    }

    // Project category enumeration
    enum ProjectCategory {
        INFRASTRUCTURE,
        HEALTHCARE,
        EDUCATION,
        ENVIRONMENT,
        SOCIAL_SERVICES,
        TECHNOLOGY,
        SECURITY,
        ECONOMIC_DEVELOPMENT,
        OTHER
    }

    // Project milestone structure
    struct ProjectMilestone {
        uint256 milestoneId;
        string title;
        string description;
        uint256 targetDate;
        uint256 completionDate;
        uint256 budgetAllocated;
        uint256 budgetSpent;
        bool isCompleted;
        string evidenceHash; // IPFS hash of completion evidence
    }

    // Main project structure
    struct Project {
        uint256 projectId;
        string title;
        string description;
        ProjectCategory category;
        ProjectStatus status;
        address governmentEntity; // Government wallet that created the project
        uint256 totalBudget;
        uint256 budgetSpent;
        uint256 startDate;
        uint256 estimatedEndDate;
        uint256 actualEndDate;
        string documentationHash; // IPFS hash for project documents
        uint256 citizenSupportScore; // Based on citizen votes/feedback
        bool isPublic; // Whether project details are public
        uint256 creationTimestamp;
        uint256 lastUpdateTimestamp;
    }

    // Storage
    uint256 private _projectIdCounter;
    mapping(uint256 => Project) public projects;
    mapping(uint256 => ProjectMilestone[]) public projectMilestones;
    mapping(uint256 => mapping(uint256 => bool)) public milestoneCompleted; // projectId => milestoneId => completed
    mapping(address => bool) public authorizedGovernmentEntities;
    mapping(uint256 => uint256[]) public categoryProjects; // category => projectIds
    mapping(address => uint256[]) public governmentEntityProjects; // entity => projectIds

    // Events
    event ProjectCreated(
        uint256 indexed projectId,
        string title,
        ProjectCategory category,
        address indexed governmentEntity,
        uint256 totalBudget
    );

    event ProjectStatusUpdated(
        uint256 indexed projectId,
        ProjectStatus oldStatus,
        ProjectStatus newStatus,
        address indexed updatedBy
    );

    event ProjectMilestoneAdded(
        uint256 indexed projectId,
        uint256 milestoneId,
        string title,
        uint256 targetDate,
        uint256 budgetAllocated
    );

    event ProjectMilestoneCompleted(
        uint256 indexed projectId,
        uint256 milestoneId,
        uint256 completionDate,
        uint256 actualBudgetSpent,
        string evidenceHash
    );

    event ProjectBudgetUpdated(
        uint256 indexed projectId,
        uint256 previousBudget,
        uint256 newBudget,
        address indexed updatedBy
    );

    event GovernmentEntityAuthorized(
        address indexed entity,
        bool authorized,
        address indexed authorizedBy
    );

    // Modifiers
    modifier onlyAuthorizedGovernment() {
        require(authorizedGovernmentEntities[msg.sender], "ProjectRegistry: Not authorized government entity");
        _;
    }

    modifier validProjectId(uint256 _projectId) {
        require(_projectId > 0 && _projectId <= _projectIdCounter, "ProjectRegistry: Invalid project ID");
        _;
    }

    modifier onlyProjectOwner(uint256 _projectId) {
        require(projects[_projectId].governmentEntity == msg.sender, "ProjectRegistry: Not project owner");
        _;
    }

    constructor(
        address initialOwner,
        address _citizenRegistry
    ) Ownable(initialOwner) {
        require(_citizenRegistry != address(0), "ProjectRegistry: Invalid citizen registry");
        citizenRegistry = CitizenIdentityRegistry(_citizenRegistry);
    }

    /**
     * @dev Create a new government project
     */
    function createProject(
        string memory _title,
        string memory _description,
        ProjectCategory _category,
        uint256 _totalBudget,
        uint256 _estimatedEndDate,
        string memory _documentationHash,
        bool _isPublic
    ) external onlyAuthorizedGovernment nonReentrant {
        require(bytes(_title).length > 0, "ProjectRegistry: Title cannot be empty");
        require(_totalBudget > 0, "ProjectRegistry: Budget must be greater than 0");
        require(_estimatedEndDate > block.timestamp, "ProjectRegistry: End date must be in future");

        _projectIdCounter++;
        uint256 newProjectId = _projectIdCounter;

        projects[newProjectId] = Project({
            projectId: newProjectId,
            title: _title,
            description: _description,
            category: _category,
            status: ProjectStatus.PROPOSED,
            governmentEntity: msg.sender,
            totalBudget: _totalBudget,
            budgetSpent: 0,
            startDate: 0, // Will be set when approved
            estimatedEndDate: _estimatedEndDate,
            actualEndDate: 0,
            documentationHash: _documentationHash,
            citizenSupportScore: 0,
            isPublic: _isPublic,
            creationTimestamp: block.timestamp,
            lastUpdateTimestamp: block.timestamp
        });

        // Add to category and entity mappings
        categoryProjects[uint256(_category)].push(newProjectId);
        governmentEntityProjects[msg.sender].push(newProjectId);

        emit ProjectCreated(newProjectId, _title, _category, msg.sender, _totalBudget);
    }

    /**
     * @dev Update project status
     */
    function updateProjectStatus(
        uint256 _projectId,
        ProjectStatus _newStatus
    ) external validProjectId(_projectId) onlyProjectOwner(_projectId) {
        Project storage project = projects[_projectId];
        ProjectStatus oldStatus = project.status;

        require(oldStatus != _newStatus, "ProjectRegistry: Status unchanged");
        require(_isValidStatusTransition(oldStatus, _newStatus), "ProjectRegistry: Invalid status transition");

        project.status = _newStatus;
        project.lastUpdateTimestamp = block.timestamp;

        // Set start date when approved
        if (_newStatus == ProjectStatus.APPROVED && project.startDate == 0) {
            project.startDate = block.timestamp;
        }

        // Set completion date when completed
        if (_newStatus == ProjectStatus.COMPLETED && project.actualEndDate == 0) {
            project.actualEndDate = block.timestamp;
        }

        emit ProjectStatusUpdated(_projectId, oldStatus, _newStatus, msg.sender);
    }

    /**
     * @dev Add milestone to project
     */
    function addProjectMilestone(
        uint256 _projectId,
        string memory _title,
        string memory _description,
        uint256 _targetDate,
        uint256 _budgetAllocated
    ) external validProjectId(_projectId) onlyProjectOwner(_projectId) {
        require(bytes(_title).length > 0, "ProjectRegistry: Milestone title cannot be empty");
        require(_targetDate > block.timestamp, "ProjectRegistry: Target date must be in future");
        require(_budgetAllocated > 0, "ProjectRegistry: Budget must be greater than 0");

        Project storage project = projects[_projectId];
        require(project.status == ProjectStatus.APPROVED || project.status == ProjectStatus.IN_PROGRESS, 
                "ProjectRegistry: Project must be approved or in progress");

        uint256 milestoneId = projectMilestones[_projectId].length + 1;

        ProjectMilestone memory newMilestone = ProjectMilestone({
            milestoneId: milestoneId,
            title: _title,
            description: _description,
            targetDate: _targetDate,
            completionDate: 0,
            budgetAllocated: _budgetAllocated,
            budgetSpent: 0,
            isCompleted: false,
            evidenceHash: ""
        });

        projectMilestones[_projectId].push(newMilestone);

        emit ProjectMilestoneAdded(_projectId, milestoneId, _title, _targetDate, _budgetAllocated);
    }

    /**
     * @dev Complete project milestone
     */
    function completeProjectMilestone(
        uint256 _projectId,
        uint256 _milestoneId,
        uint256 _actualBudgetSpent,
        string memory _evidenceHash
    ) external validProjectId(_projectId) onlyProjectOwner(_projectId) {
        require(_milestoneId > 0 && _milestoneId <= projectMilestones[_projectId].length, 
                "ProjectRegistry: Invalid milestone ID");
        require(!milestoneCompleted[_projectId][_milestoneId], "ProjectRegistry: Milestone already completed");
        require(bytes(_evidenceHash).length > 0, "ProjectRegistry: Evidence hash required");

        ProjectMilestone storage milestone = projectMilestones[_projectId][_milestoneId - 1];
        Project storage project = projects[_projectId];

        require(_actualBudgetSpent <= milestone.budgetAllocated, "ProjectRegistry: Spent exceeds allocated budget");
        require(project.budgetSpent + _actualBudgetSpent <= project.totalBudget, 
                "ProjectRegistry: Total spent would exceed project budget");

        // Update milestone
        milestone.isCompleted = true;
        milestone.completionDate = block.timestamp;
        milestone.budgetSpent = _actualBudgetSpent;
        milestone.evidenceHash = _evidenceHash;
        milestoneCompleted[_projectId][_milestoneId] = true;

        // Update project budget
        project.budgetSpent += _actualBudgetSpent;
        project.lastUpdateTimestamp = block.timestamp;

        // Update project status to in progress if not already
        if (project.status == ProjectStatus.APPROVED) {
            project.status = ProjectStatus.IN_PROGRESS;
            emit ProjectStatusUpdated(_projectId, ProjectStatus.APPROVED, ProjectStatus.IN_PROGRESS, msg.sender);
        }

        emit ProjectMilestoneCompleted(_projectId, _milestoneId, block.timestamp, _actualBudgetSpent, _evidenceHash);
    }

    /**
     * @dev Update project budget (owner or admin only)
     */
    function updateProjectBudget(
        uint256 _projectId,
        uint256 _newBudget
    ) external validProjectId(_projectId) {
        require(
            msg.sender == projects[_projectId].governmentEntity || msg.sender == owner(),
            "ProjectRegistry: Not authorized to update budget"
        );
        require(_newBudget > 0, "ProjectRegistry: Budget must be greater than 0");

        Project storage project = projects[_projectId];
        require(_newBudget >= project.budgetSpent, "ProjectRegistry: New budget less than already spent");

        uint256 previousBudget = project.totalBudget;
        project.totalBudget = _newBudget;
        project.lastUpdateTimestamp = block.timestamp;

        emit ProjectBudgetUpdated(_projectId, previousBudget, _newBudget, msg.sender);
    }

    /**
     * @dev Update citizen support score (called by voting contract)
     */
    function updateCitizenSupportScore(
        uint256 _projectId,
        uint256 _supportScore
    ) external validProjectId(_projectId) {
        // TODO: Add access control for voting contract
        // require(msg.sender == votingContract, "ProjectRegistry: Only voting contract can update support");

        projects[_projectId].citizenSupportScore = _supportScore;
        projects[_projectId].lastUpdateTimestamp = block.timestamp;
    }

    // View functions
    function getProject(uint256 _projectId) external view validProjectId(_projectId) returns (Project memory) {
        Project memory project = projects[_projectId];
        require(project.isPublic || msg.sender == project.governmentEntity || msg.sender == owner(), 
                "ProjectRegistry: Project is not public");
        return project;
    }

    function getProjectMilestones(uint256 _projectId) external view validProjectId(_projectId) returns (ProjectMilestone[] memory) {
        Project memory project = projects[_projectId];
        require(project.isPublic || msg.sender == project.governmentEntity || msg.sender == owner(), 
                "ProjectRegistry: Project is not public");
        return projectMilestones[_projectId];
    }

    function getProjectsByCategory(ProjectCategory _category) external view returns (uint256[] memory) {
        return categoryProjects[uint256(_category)];
    }

    function getProjectsByGovernmentEntity(address _entity) external view returns (uint256[] memory) {
        return governmentEntityProjects[_entity];
    }

    function getProjectsByStatus(ProjectStatus _status) external view returns (uint256[] memory) {
        uint256[] memory result = new uint256[](_projectIdCounter);
        uint256 count = 0;

        for (uint256 i = 1; i <= _projectIdCounter; i++) {
            if (projects[i].status == _status && (projects[i].isPublic || msg.sender == owner())) {
                result[count] = i;
                count++;
            }
        }

        // Resize array to actual count
        uint256[] memory finalResult = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            finalResult[i] = result[i];
        }

        return finalResult;
    }

    function getProjectStats() external view returns (
        uint256 totalProjects,
        uint256 approvedProjects,
        uint256 inProgressProjects,
        uint256 completedProjects,
        uint256 totalBudgetAllocated,
        uint256 totalBudgetSpent
    ) {
        uint256 approved = 0;
        uint256 inProgress = 0;
        uint256 completed = 0;
        uint256 totalAllocated = 0;
        uint256 totalSpent = 0;

        for (uint256 i = 1; i <= _projectIdCounter; i++) {
            Project storage project = projects[i];

            if (project.status == ProjectStatus.APPROVED) approved++;
            else if (project.status == ProjectStatus.IN_PROGRESS) inProgress++;
            else if (project.status == ProjectStatus.COMPLETED) completed++;

            totalAllocated += project.totalBudget;
            totalSpent += project.budgetSpent;
        }

        return (_projectIdCounter, approved, inProgress, completed, totalAllocated, totalSpent);
    }

    function getTotalProjects() external view returns (uint256) {
        return _projectIdCounter;
    }

    function getProjectProgress(uint256 _projectId) external view validProjectId(_projectId) returns (
        uint256 totalMilestones,
        uint256 completedMilestones,
        uint256 budgetUtilization // percentage in basis points (10000 = 100%)
    ) {
        Project memory project = projects[_projectId];
        require(project.isPublic || msg.sender == project.governmentEntity || msg.sender == owner(), 
                "ProjectRegistry: Project is not public");

        ProjectMilestone[] memory milestones = projectMilestones[_projectId];
        uint256 completed = 0;

        for (uint256 i = 0; i < milestones.length; i++) {
            if (milestones[i].isCompleted) {
                completed++;
            }
        }

        uint256 utilization = project.totalBudget > 0 ? 
                             (project.budgetSpent * 10000) / project.totalBudget : 0;

        return (milestones.length, completed, utilization);
    }

    // Internal functions
    function _isValidStatusTransition(ProjectStatus _from, ProjectStatus _to) internal pure returns (bool) {
        if (_from == ProjectStatus.PROPOSED) {
            return _to == ProjectStatus.APPROVED || _to == ProjectStatus.CANCELLED;
        } else if (_from == ProjectStatus.APPROVED) {
            return _to == ProjectStatus.IN_PROGRESS || _to == ProjectStatus.CANCELLED;
        } else if (_from == ProjectStatus.IN_PROGRESS) {
            return _to == ProjectStatus.COMPLETED || _to == ProjectStatus.UNDER_REVIEW || _to == ProjectStatus.CANCELLED;
        } else if (_from == ProjectStatus.UNDER_REVIEW) {
            return _to == ProjectStatus.COMPLETED || _to == ProjectStatus.IN_PROGRESS;
        }
        return false; // COMPLETED and CANCELLED are final states
    }

    // Admin functions
    function authorizeGovernmentEntity(address _entity, bool _authorized) external onlyOwner {
        require(_entity != address(0), "ProjectRegistry: Invalid entity address");
        authorizedGovernmentEntities[_entity] = _authorized;
        emit GovernmentEntityAuthorized(_entity, _authorized, msg.sender);
    }

    function emergencyUpdateProject(
        uint256 _projectId,
        ProjectStatus _status,
        uint256 _newBudget
    ) external onlyOwner validProjectId(_projectId) {
        Project storage project = projects[_projectId];
        ProjectStatus oldStatus = project.status;

        if (_status != oldStatus) {
            project.status = _status;
            emit ProjectStatusUpdated(_projectId, oldStatus, _status, msg.sender);
        }

        if (_newBudget != project.totalBudget && _newBudget >= project.budgetSpent) {
            uint256 previousBudget = project.totalBudget;
            project.totalBudget = _newBudget;
            emit ProjectBudgetUpdated(_projectId, previousBudget, _newBudget, msg.sender);
        }

        project.lastUpdateTimestamp = block.timestamp;
    }

    function setProjectPublic(uint256 _projectId, bool _isPublic) external onlyOwner validProjectId(_projectId) {
        projects[_projectId].isPublic = _isPublic;
        projects[_projectId].lastUpdateTimestamp = block.timestamp;
    }
}","keccak256":"0x82263b72f0b4d03e19ff3e708c65bc90b901705625b820770a74107879ef7ce2","license":"MIT"},"contracts/TreasuryFundTracking.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./GovernmentProjectRegistry.sol";
import "./CitizenIdentityRegistry.sol";

/**
 * @title TreasuryFundTracking
 * @dev Handles all financial transparency and fund allocation tracking
 * @author CitiProof Team
 */
contract TreasuryFundTracking is Ownable, ReentrancyGuard {

    GovernmentProjectRegistry public immutable projectRegistry;
    CitizenIdentityRegistry public immutable citizenRegistry;

    // Fund source enumeration
    enum FundSource {
        GOVERNMENT_BUDGET,
        DONOR_CONTRIBUTION,
        GRANTS,
        FUNDRAISING,
        OTHER
    }

    // Transaction type enumeration
    enum TransactionType {
        ALLOCATION,    // Funds allocated to project
        EXPENDITURE,   // Actual spending
        REFUND,        // Funds returned
        TRANSFER,      // Inter-project transfer
        DONATION_IN,   // Donation received
        WITHDRAWAL     // Emergency withdrawal
    }

    // Fund allocation structure
    struct FundAllocation {
        uint256 allocationId;
        uint256 projectId;
        uint256 amount;
        FundSource source;
        address authorizedBy;
        uint256 timestamp;
        string ipfsDescriptionHash; // IPFS hash for detailed description
        bool isActive;
        string documentHash; // IPFS hash for supporting documents
    }

    // Financial transaction structure
    struct FinancialTransaction {
        uint256 transactionId;
        uint256 projectId;
        uint256 amount;
        TransactionType transactionType;
        FundSource source;
        address authorizedBy;
        address recipient; // Can be contractor, vendor, etc.
        uint256 timestamp;
        string ipfsDescriptionHash; // IPFS hash for detailed description
        string receiptHash; // IPFS hash for receipt/proof
        bool isVerified;
        address verifiedBy;
        uint256 verificationTimestamp;
    }

    // Donor contribution structure
    struct DonorContribution {
        uint256 contributionId;
        address donor;
        uint256 amount;
        uint256 projectId; // 0 for general fund
        uint256 timestamp;
        string ipfsMessageHash; // IPFS hash for donation message
        bool isAnonymous;
        bool isRefunded;
    }

    // Budget allocation structure
    struct BudgetAllocation {
        uint256 totalBudget;
        uint256 allocatedBudget;
        uint256 spentBudget;
        uint256 availableBudget;
        mapping(uint256 => uint256) projectAllocations; // projectId => allocated amount
        mapping(uint256 => uint256) projectSpending;    // projectId => spent amount
    }

    // Storage
    uint256 private _allocationIdCounter;
    uint256 private _transactionIdCounter;
    uint256 private _contributionIdCounter;

    BudgetAllocation private budget;
    mapping(uint256 => FundAllocation) public allocations;
    mapping(uint256 => FinancialTransaction) public transactions;
    mapping(uint256 => DonorContribution) public contributions;
    mapping(uint256 => uint256[]) public projectAllocations; // projectId => allocationIds
    mapping(uint256 => uint256[]) public projectTransactions; // projectId => transactionIds
    mapping(address => uint256[]) public donorContributions; // donor => contributionIds
    mapping(address => bool) public authorizedTreasurers;

    // Events
    event FundsAllocated(
        uint256 indexed allocationId,
        uint256 indexed projectId,
        uint256 amount,
        FundSource source,
        address indexed authorizedBy
    );

    event TransactionRecorded(
        uint256 indexed transactionId,
        uint256 indexed projectId,
        uint256 amount,
        TransactionType transactionType,
        address indexed recipient
    );

    event DonationReceived(
        uint256 indexed contributionId,
        address indexed donor,
        uint256 amount,
        uint256 indexed projectId
    );

    event TransactionVerified(
        uint256 indexed transactionId,
        address indexed verifiedBy,
        uint256 verificationTimestamp
    );

    event BudgetUpdated(
        uint256 previousTotal,
        uint256 newTotal,
        address indexed updatedBy
    );

    event TreasurerAuthorized(
        address indexed treasurer,
        bool authorized,
        address indexed authorizedBy
    );

    event EmergencyWithdrawal(
        uint256 amount,
        address indexed recipient,
        string reason,
        address indexed authorizedBy
    );

    // Modifiers
    modifier onlyAuthorizedTreasurer() {
        require(authorizedTreasurers[msg.sender] || msg.sender == owner(), 
                "Treasury: Not authorized treasurer");
        _;
    }

    modifier validProjectId(uint256 _projectId) {
        require(_projectId > 0 && _projectId <= projectRegistry.getTotalProjects(), 
                "Treasury: Invalid project ID");
        _;
    }

    modifier validAllocationId(uint256 _allocationId) {
        require(_allocationId > 0 && _allocationId <= _allocationIdCounter, 
                "Treasury: Invalid allocation ID");
        _;
    }

    modifier validTransactionId(uint256 _transactionId) {
        require(_transactionId > 0 && _transactionId <= _transactionIdCounter, 
                "Treasury: Invalid transaction ID");
        _;
    }

    constructor(
        address initialOwner,
        address _projectRegistry,
        address _citizenRegistry,
        uint256 _initialBudget
    ) Ownable(initialOwner) {
        require(_projectRegistry != address(0), "Treasury: Invalid project registry");
        require(_citizenRegistry != address(0), "Treasury: Invalid citizen registry");

        projectRegistry = GovernmentProjectRegistry(_projectRegistry);
        citizenRegistry = CitizenIdentityRegistry(_citizenRegistry);

        budget.totalBudget = _initialBudget;
        budget.availableBudget = _initialBudget;
    }

    /**
     * @dev Allocate funds to a specific project
     */
    function allocateFundsToProject(
        uint256 _projectId,
        uint256 _amount,
        FundSource _source,
        string memory _ipfsDescriptionHash,
        string memory _documentHash
    ) external onlyAuthorizedTreasurer validProjectId(_projectId) nonReentrant {
        require(_amount > 0, "Treasury: Amount must be greater than 0");
        require(budget.availableBudget >= _amount, "Treasury: Insufficient available budget");
        require(bytes(_ipfsDescriptionHash).length > 0, "Treasury: IPFS description hash required");

        _allocationIdCounter++;
        uint256 newAllocationId = _allocationIdCounter;

        // Create allocation record
        allocations[newAllocationId] = FundAllocation({
            allocationId: newAllocationId,
            projectId: _projectId,
            amount: _amount,
            source: _source,
            authorizedBy: msg.sender,
            timestamp: block.timestamp,
            ipfsDescriptionHash: _ipfsDescriptionHash,
            isActive: true,
            documentHash: _documentHash
        });

        // Update budget tracking
        budget.allocatedBudget += _amount;
        budget.availableBudget -= _amount;
        budget.projectAllocations[_projectId] += _amount;

        // Add to project allocations
        projectAllocations[_projectId].push(newAllocationId);

        emit FundsAllocated(newAllocationId, _projectId, _amount, _source, msg.sender);
    }

    /**
     * @dev Record a financial transaction (expenditure)
     */
    function recordTransaction(
        uint256 _projectId,
        uint256 _amount,
        TransactionType _transactionType,
        FundSource _source,
        address _recipient,
        string memory _ipfsDescriptionHash,
        string memory _receiptHash
    ) external onlyAuthorizedTreasurer validProjectId(_projectId) nonReentrant {
        require(_amount > 0, "Treasury: Amount must be greater than 0");
        require(_recipient != address(0), "Treasury: Invalid recipient address");
        require(bytes(_ipfsDescriptionHash).length > 0, "Treasury: IPFS description hash required");

        // Validate sufficient allocation for expenditures
        if (_transactionType == TransactionType.EXPENDITURE) {
            uint256 availableForProject = budget.projectAllocations[_projectId] - budget.projectSpending[_projectId];
            require(availableForProject >= _amount, "Treasury: Insufficient project allocation");

            // Update spending tracking
            budget.spentBudget += _amount;
            budget.projectSpending[_projectId] += _amount;
        }

        _transactionIdCounter++;
        uint256 newTransactionId = _transactionIdCounter;

        // Create transaction record
        transactions[newTransactionId] = FinancialTransaction({
            transactionId: newTransactionId,
            projectId: _projectId,
            amount: _amount,
            transactionType: _transactionType,
            source: _source,
            authorizedBy: msg.sender,
            recipient: _recipient,
            timestamp: block.timestamp,
            ipfsDescriptionHash: _ipfsDescriptionHash,
            receiptHash: _receiptHash,
            isVerified: false,
            verifiedBy: address(0),
            verificationTimestamp: 0
        });

        // Add to project transactions
        projectTransactions[_projectId].push(newTransactionId);

        emit TransactionRecorded(newTransactionId, _projectId, _amount, _transactionType, _recipient);
    }

    /**
     * @dev Receive donor contribution
     */
    function receiveDonation(
        uint256 _projectId,
        string memory _ipfsMessageHash,
        bool _isAnonymous
    ) external payable nonReentrant {
        require(msg.value > 0, "Treasury: Donation amount must be greater than 0");

        // If project ID is provided, validate it
        if (_projectId > 0) {
            require(_projectId <= projectRegistry.getTotalProjects(), "Treasury: Invalid project ID");
        }

        _contributionIdCounter++;
        uint256 newContributionId = _contributionIdCounter;

        // Create contribution record
        contributions[newContributionId] = DonorContribution({
            contributionId: newContributionId,
            donor: msg.sender,
            amount: msg.value,
            projectId: _projectId,
            timestamp: block.timestamp,
            ipfsMessageHash: _ipfsMessageHash,
            isAnonymous: _isAnonymous,
            isRefunded: false
        });

        // Update budget
        budget.totalBudget += msg.value;
        budget.availableBudget += msg.value;

        // Add to donor contributions
        donorContributions[msg.sender].push(newContributionId);

        emit DonationReceived(newContributionId, msg.sender, msg.value, _projectId);
    }

    /**
     * @dev Verify a transaction (by authorized verifiers)
     */
    function verifyTransaction(uint256 _transactionId) external validTransactionId(_transactionId) {
        // Check if caller is authorized (government entity, treasurer, or admin)
        require(
            authorizedTreasurers[msg.sender] || 
            msg.sender == owner() ||
            _isAuthorizedVerifier(msg.sender),
            "Treasury: Not authorized to verify transactions"
        );

        FinancialTransaction storage transaction = transactions[_transactionId];
        require(!transaction.isVerified, "Treasury: Transaction already verified");
        require(transaction.authorizedBy != msg.sender, "Treasury: Cannot verify own transaction");

        transaction.isVerified = true;
        transaction.verifiedBy = msg.sender;
        transaction.verificationTimestamp = block.timestamp;

        emit TransactionVerified(_transactionId, msg.sender, block.timestamp);
    }

    /**
     * @dev Transfer funds between projects
     */
    function transferFundsBetweenProjects(
        uint256 _fromProjectId,
        uint256 _toProjectId,
        uint256 _amount,
        string memory _reason
    ) external onlyAuthorizedTreasurer nonReentrant {
        require(_fromProjectId != _toProjectId, "Treasury: Cannot transfer to same project");
        require(_amount > 0, "Treasury: Amount must be greater than 0");
        require(bytes(_reason).length > 0, "Treasury: Reason required");

        // Validate projects exist
        require(_fromProjectId <= projectRegistry.getTotalProjects(), "Treasury: Invalid from project");
        require(_toProjectId <= projectRegistry.getTotalProjects(), "Treasury: Invalid to project");

        // Check available funds in source project
        uint256 availableInSource = budget.projectAllocations[_fromProjectId] - budget.projectSpending[_fromProjectId];
        require(availableInSource >= _amount, "Treasury: Insufficient funds in source project");

        // Update allocations
        budget.projectAllocations[_fromProjectId] -= _amount;
        budget.projectAllocations[_toProjectId] += _amount;

        // Record transfer transactions
        _transactionIdCounter++;
        uint256 transferOutId = _transactionIdCounter;

        transactions[transferOutId] = FinancialTransaction({
            transactionId: transferOutId,
            projectId: _fromProjectId,
            amount: _amount,
            transactionType: TransactionType.TRANSFER,
            source: FundSource.GOVERNMENT_BUDGET,
            authorizedBy: msg.sender,
            recipient: address(this),
            timestamp: block.timestamp,
            ipfsDescriptionHash: "", // Simple transfer descriptions don't need IPFS
            receiptHash: "",
            isVerified: true, // Auto-verified for internal transfers
            verifiedBy: msg.sender,
            verificationTimestamp: block.timestamp
        });

        projectTransactions[_fromProjectId].push(transferOutId);

        _transactionIdCounter++;
        uint256 transferInId = _transactionIdCounter;

        transactions[transferInId] = FinancialTransaction({
            transactionId: transferInId,
            projectId: _toProjectId,
            amount: _amount,
            transactionType: TransactionType.ALLOCATION,
            source: FundSource.GOVERNMENT_BUDGET,
            authorizedBy: msg.sender,
            recipient: address(this),
            timestamp: block.timestamp,
            ipfsDescriptionHash: "", // Simple transfer descriptions don't need IPFS
            receiptHash: "",
            isVerified: true, // Auto-verified for internal transfers
            verifiedBy: msg.sender,
            verificationTimestamp: block.timestamp
        });

        projectTransactions[_toProjectId].push(transferInId);

        emit TransactionRecorded(transferOutId, _fromProjectId, _amount, TransactionType.TRANSFER, address(this));
        emit TransactionRecorded(transferInId, _toProjectId, _amount, TransactionType.ALLOCATION, address(this));
    }

    // View functions
    function getBudgetOverview() external view returns (
        uint256 totalBudget,
        uint256 allocatedBudget,
        uint256 spentBudget,
        uint256 availableBudget,
        uint256 utilizationPercentage
    ) {
        uint256 utilization = budget.totalBudget > 0 ? (budget.spentBudget * 10000) / budget.totalBudget : 0;

        return (
            budget.totalBudget,
            budget.allocatedBudget,
            budget.spentBudget,
            budget.availableBudget,
            utilization
        );
    }

    function getProjectFinancials(uint256 _projectId) external view validProjectId(_projectId) returns (
        uint256 allocatedAmount,
        uint256 spentAmount,
        uint256 availableAmount,
        uint256 transactionCount,
        uint256 utilizationPercentage
    ) {
        uint256 allocated = budget.projectAllocations[_projectId];
        uint256 spent = budget.projectSpending[_projectId];
        uint256 available = allocated > spent ? allocated - spent : 0;
        uint256 utilization = allocated > 0 ? (spent * 10000) / allocated : 0;

        return (
            allocated,
            spent,
            available,
            projectTransactions[_projectId].length,
            utilization
        );
    }

    function getProjectTransactions(uint256 _projectId) external view validProjectId(_projectId) returns (uint256[] memory) {
        return projectTransactions[_projectId];
    }

    function getProjectAllocations(uint256 _projectId) external view validProjectId(_projectId) returns (uint256[] memory) {
        return projectAllocations[_projectId];
    }

    function getDonorContributions(address _donor) external view returns (uint256[] memory) {
        return donorContributions[_donor];
    }

    function getTotalContributions() external view returns (uint256 totalAmount, uint256 totalCount) {
        uint256 total = 0;
        for (uint256 i = 1; i <= _contributionIdCounter; i++) {
            if (!contributions[i].isRefunded) {
                total += contributions[i].amount;
            }
        }
        return (total, _contributionIdCounter);
    }

    function getUnverifiedTransactions() external view returns (uint256[] memory) {
        uint256 count = 0;

        // Count unverified transactions
        for (uint256 i = 1; i <= _transactionIdCounter; i++) {
            if (!transactions[i].isVerified) {
                count++;
            }
        }

        // Create result array
        uint256[] memory unverifiedIds = new uint256[](count);
        uint256 index = 0;

        for (uint256 i = 1; i <= _transactionIdCounter; i++) {
            if (!transactions[i].isVerified) {
                unverifiedIds[index] = i;
                index++;
            }
        }

        return unverifiedIds;
    }

    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    // Internal functions
    function _isAuthorizedVerifier(address _verifier) internal view returns (bool) {
        // Check if verifier is a registered citizen with sufficient reputation
        try citizenRegistry.isCitizenRegistered(_verifier) returns (bool isRegistered) {
            if (!isRegistered) return false;

            // Add reputation threshold check when reputation system is integrated
            // For now, any registered citizen can verify
            return true;
        } catch {
            return false;
        }
    }

    // Admin functions
    function updateBudget(uint256 _newTotalBudget) external onlyOwner {
        require(_newTotalBudget >= budget.spentBudget, "Treasury: New budget less than already spent");

        uint256 previousBudget = budget.totalBudget;

        if (_newTotalBudget > budget.totalBudget) {
            // Budget increase
            budget.availableBudget += (_newTotalBudget - budget.totalBudget);
        } else if (_newTotalBudget < budget.totalBudget) {
            // Budget decrease
            uint256 decrease = budget.totalBudget - _newTotalBudget;
            budget.availableBudget = budget.availableBudget > decrease ? budget.availableBudget - decrease : 0;
        }

        budget.totalBudget = _newTotalBudget;

        emit BudgetUpdated(previousBudget, _newTotalBudget, msg.sender);
    }

    function authorizeTreasurer(address _treasurer, bool _authorized) external onlyOwner {
        require(_treasurer != address(0), "Treasury: Invalid treasurer address");
        authorizedTreasurers[_treasurer] = _authorized;
        emit TreasurerAuthorized(_treasurer, _authorized, msg.sender);
    }

    function emergencyWithdraw(uint256 _amount, address payable _recipient, string memory _reason) external onlyOwner {
        require(_amount > 0, "Treasury: Amount must be greater than 0");
        require(_recipient != address(0), "Treasury: Invalid recipient");
        require(address(this).balance >= _amount, "Treasury: Insufficient contract balance");
        require(bytes(_reason).length > 0, "Treasury: Reason required");

        _recipient.transfer(_amount);

        emit EmergencyWithdrawal(_amount, _recipient, _reason, msg.sender);
    }

    function refundDonation(uint256 _contributionId) external onlyOwner {
        require(_contributionId > 0 && _contributionId <= _contributionIdCounter, "Treasury: Invalid contribution ID");

        DonorContribution storage contribution = contributions[_contributionId];
        require(!contribution.isRefunded, "Treasury: Already refunded");
        require(address(this).balance >= contribution.amount, "Treasury: Insufficient balance for refund");

        contribution.isRefunded = true;
        budget.totalBudget -= contribution.amount;
        budget.availableBudget = budget.availableBudget > contribution.amount ? 
                                budget.availableBudget - contribution.amount : 0;

        payable(contribution.donor).transfer(contribution.amount);
    }

    // Allow contract to receive donations
    receive() external payable {
        // Donations received without explicit project assignment go to general fund
        _contributionIdCounter++;
        uint256 newContributionId = _contributionIdCounter;

        contributions[newContributionId] = DonorContribution({
            contributionId: newContributionId,
            donor: msg.sender,
            amount: msg.value,
            projectId: 0, // General fund
            timestamp: block.timestamp,
            ipfsMessageHash: "", // Simple direct donation message
            isAnonymous: false,
            isRefunded: false
        });

        budget.totalBudget += msg.value;
        budget.availableBudget += msg.value;

        donorContributions[msg.sender].push(newContributionId);

        emit DonationReceived(newContributionId, msg.sender, msg.value, 0);
    }
